import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PostCard = ({ post, onUpdate }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [userRating, setUserRating] = useState(0);  // คะแนน user เคยให้
  const [showComments, setShowComments] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (showComments) {
      fetchComments();
    }
  }, [showComments]);

  // ตั้งค่า userRating จาก post.user_rating (มาจาก backend)
  useEffect(() => {
    setUserRating(post.user_rating || 0);
  }, [post.user_rating]);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/posts/${post.id}/comments`);
      setComments(response.data);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleAddComment = async e => {
    e.preventDefault();
    if (!newComment.trim()) return;
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:5000/api/posts/${post.id}/comments`,
        { comment: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewComment('');
      fetchComments();
      onUpdate();
    } catch (error) {
      console.error('Error adding comment:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRating = async rating => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:5000/api/posts/${post.id}/rating`,
        { rating },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUserRating(rating);
      onUpdate();
    } catch (error) {
      console.error('Error adding rating:', error);
    }
  };

  const renderStars = (rating, interactive = false) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span
          key={i}
          style={{
            cursor: interactive ? 'pointer' : 'default',
            color: i <= rating ? '#f39c12' : '#ccc',
            fontSize: '20px',
            userSelect: 'none',
          }}
          onClick={interactive ? () => handleRating(i) : undefined}
          role={interactive ? 'button' : undefined}
          aria-label={interactive ? `ให้คะแนน ${i} ดาว` : undefined}
        >
          ★
        </span>
      );
    }
    return stars;
  };

  const formatDate = dateString =>
    new Date(dateString).toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });

  const ratingValue = Number(post.average_rating);
  const displayRating = isNaN(ratingValue) ? 'ไม่มีคะแนน' : ratingValue.toFixed(1);

  return (
    <div style={{ border: '1px solid #ddd', padding: 16, borderRadius: 8, marginBottom: 20 }}>
      <h3>{post.shop_name}</h3>
      {post.image_url && (
        <img
          src={`http://localhost:5000${post.image_url}`}
          alt={post.shop_name}
          style={{ width: '100%', maxHeight: 300, objectFit: 'cover', borderRadius: 8 }}
        />
      )}
      <p>{post.description}</p>
      <div>
        <strong>คะแนนเฉลี่ย: </strong>
        {renderStars(Math.round(ratingValue))}
        <span style={{ marginLeft: 8 }}>
          {displayRating} ({post.total_ratings || 0} คะแนน)
        </span>
      </div>

      <div style={{ marginTop: 8 }}>
        <strong>ให้คะแนนร้านนี้: </strong>
        {renderStars(userRating, true)}
      </div>

      <button
        onClick={() => setShowComments(!showComments)}
        style={{ marginTop: 12, padding: '6px 12px', background: '#007bff', color: '#fff', border: 'none', borderRadius: 4 }}
      >
        {showComments ? 'ซ่อนความคิดเห็น' : 'แสดงความคิดเห็น'}
      </button>

      {showComments && (
        <div style={{ marginTop: 12 }}>
          <form onSubmit={handleAddComment}>
            <textarea
              value={newComment}
              onChange={e => setNewComment(e.target.value)}
              rows="3"
              placeholder="เขียนความคิดเห็น..."
              required
              style={{ width: '100%', padding: 8, resize: 'vertical' }}
            />
            <button
              type="submit"
              disabled={loading || !newComment.trim()}
              style={{ marginTop: 8, padding: '8px 16px', background: '#28a745', color: '#fff', border: 'none', borderRadius: 4 }}
            >
              {loading ? 'กำลังส่ง...' : 'ส่งความคิดเห็น'}
            </button>
          </form>

          <div style={{ marginTop: 12 }}>
            {comments.length === 0 ? (
              <p>ยังไม่มีความคิดเห็น</p>
            ) : (
              comments.map(comment => (
                <div key={comment.id} style={{ borderBottom: '1px solid #eee', paddingBottom: 8, marginBottom: 8 }}>
                  <div style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
                    {comment.profile_image && (
                      <img
                        src={`http://localhost:5000${comment.profile_image}`}
                        alt={comment.username}
                        style={{ width: 30, height: 30, borderRadius: '50%', marginRight: 8 }}
                      />
                    )}
                    <strong>{comment.username}</strong>
                    <small style={{ marginLeft: 'auto', color: '#999' }}>{formatDate(comment.created_at)}</small>
                  </div>
                  <p>{comment.comment}</p>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default PostCard;
