import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PostCard from './PostCard';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchPosts = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/posts', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPosts(res.data);
    } catch (err) {
      setError('ไม่สามารถโหลดโพสต์ได้');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  if (loading) return <div className="text-center mt-10">กำลังโหลดโพสต์...</div>;
  if (error) return <div className="text-center mt-10 text-red-500">{error}</div>;

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6 text-center">ร้านค้าแนะนำ</h1>
      {posts.length === 0 && (
        <p className="text-center text-gray-500">ยังไม่มีโพสต์ร้านค้า</p>
      )}
      <div className="space-y-6">
        {posts.map(post => (
          <PostCard key={post.id} post={post} onUpdate={fetchPosts} />
        ))}
      </div>
    </div>
  );
};

export default Home;
