import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PostCard = ({ post, onUpdate }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [userRating, setUserRating] = useState(0);
  const [showComments, setShowComments] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (showComments) fetchComments();
  }, [showComments]);

  useEffect(() => {
    setUserRating(post.user_rating || 0);
  }, [post.user_rating]);

  const fetchComments = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/posts/${post.id}/comments`);
      setComments(res.data);
    } catch (e) {
      console.error(e);
    }
  };

  const handleAddComment = async e => {
    e.preventDefault();
    if (!newComment.trim()) return;
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:5000/api/posts/${post.id}/comments`,
        { comment: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewComment('');
      fetchComments();
      onUpdate();
    } catch (e) {
      console.error(e);
    } finally {
      setLoading(false);
    }
  };

  const handleRating = async rating => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `http://localhost:5000/api/posts/${post.id}/rating`,
        { rating },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUserRating(rating);
      onUpdate();
    } catch (e) {
      console.error(e);
    }
  };

  const renderStars = (rating, interactive = false) => {
    return [...Array(5)].map((_, i) => {
      const starNum = i + 1;
      return (
        <button
          key={starNum}
          onClick={interactive ? () => handleRating(starNum) : undefined}
          className={`text-2xl ${starNum <= rating ? 'text-yellow-400' : 'text-gray-300'} ${
            interactive ? 'hover:text-yellow-500' : ''
          }`}
          aria-label={interactive ? `ให้คะแนน ${starNum} ดาว` : undefined}
          type={interactive ? 'button' : 'span'}
        >
          ★
        </button>
      );
    });
  };

  const formatDate = dateString =>
    new Date(dateString).toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });

  const ratingValue = Number(post.average_rating);
  const displayRating = isNaN(ratingValue) ? 'ไม่มีคะแนน' : ratingValue.toFixed(1);

  return (
    <div className="bg-white shadow-md rounded-lg p-6 mb-6 max-w-3xl mx-auto">
      <h2 className="text-xl font-semibold mb-2">{post.shop_name}</h2>
      {post.image_url && (
        <img
          src={`http://localhost:5000${post.image_url}`}
          alt={post.shop_name}
          className="w-full h-64 object-cover rounded-md mb-4"
        />
      )}
      <p className="mb-4 text-gray-700">{post.description}</p>

      <div className="flex items-center space-x-2 mb-3">
        <div className="flex">{renderStars(Math.round(ratingValue))}</div>
        <span className="ml-2 text-gray-600 text-sm">
          {displayRating} ({post.total_ratings || 0} คะแนน)
        </span>
      </div>

      <div className="mb-4">
        <span className="font-medium">ให้คะแนนร้านนี้: </span>
        <div className="inline-flex">{renderStars(userRating, true)}</div>
      </div>

      <button
        onClick={() => setShowComments(!showComments)}
        className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition"
      >
        {showComments ? 'ซ่อนความคิดเห็น' : 'แสดงความคิดเห็น'}
      </button>

      {showComments && (
        <div className="mt-6">
          <form onSubmit={handleAddComment} className="mb-4">
            <textarea
              value={newComment}
              onChange={e => setNewComment(e.target.value)}
              rows="3"
              placeholder="เขียนความคิดเห็น..."
              required
              className="w-full border border-gray-300 rounded p-2 resize-y focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button
              type="submit"
              disabled={loading || !newComment.trim()}
              className={`mt-2 px-4 py-2 rounded text-white ${
                loading || !newComment.trim()
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-green-600 hover:bg-green-700'
              } transition`}
            >
              {loading ? 'กำลังส่ง...' : 'ส่งความคิดเห็น'}
            </button>
          </form>

          {comments.length === 0 ? (
            <p className="text-gray-500">ยังไม่มีความคิดเห็น</p>
          ) : (
            <div className="space-y-4">
              {comments.map(comment => (
                <div key={comment.id} className="border-b pb-3">
                  <div className="flex items-center mb-1">
                    {comment.profile_image && (
                      <img
                        src={`http://localhost:5000${comment.profile_image}`}
                        alt={comment.username}
                        className="w-8 h-8 rounded-full mr-3"
                      />
                    )}
                    <span className="font-semibold">{comment.username}</span>
                    <span className="ml-auto text-sm text-gray-400">
                      {formatDate(comment.created_at)}
                    </span>
                  </div>
                  <p className="text-gray-700">{comment.comment}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PostCard;
