import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PostCard from './PostCard';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  // ดึงข้อมูล user จาก token หรือ API
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    // สมมุติ backend มี endpoint /api/profile
    axios
      .get('http://localhost:5000/api/profile', { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setUser(res.data))
      .catch(() => setUser(null));
  }, []);

  const fetchPosts = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/posts', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPosts(res.data);
    } catch (err) {
      setError('ไม่สามารถโหลดโพสต์ได้');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  if (loading) return <div>กำลังโหลดโพสต์...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>ร้านค้าแนะนำ</h1>
      {posts.length === 0 && <p>ยังไม่มีโพสต์ร้านค้า</p>}
      {posts.map(post => (
        <PostCard key={post.id} post={post} currentUser={user} onUpdate={fetchPosts} />
      ))}
    </div>
  );
};

export default Home;
